import multer from "multer";
import path from "path";
import fs from "fs";
import { Request } from "express";

// Create upload directory if it doesn't exist
const uploadDir = path.join(process.cwd(), 'uploads');
if (!fs.existsSync(uploadDir)) {
  fs.mkdirSync(uploadDir, { recursive: true });
}

// Create subdirectories for different file types
const subDirs = ['documents', 'certificates', 'qr-codes', 'invoices', 'reports'];
subDirs.forEach(dir => {
  const dirPath = path.join(uploadDir, dir);
  if (!fs.existsSync(dirPath)) {
    fs.mkdirSync(dirPath, { recursive: true });
  }
});

// Configure multer storage
const storage = multer.diskStorage({
  destination: (req: Request, file: Express.Multer.File, cb) => {
    // Determine upload directory based on file type or route
    let uploadPath = path.join(uploadDir, 'documents');

    if (req.path.includes('certificates')) {
      uploadPath = path.join(uploadDir, 'certificates');
    } else if (req.path.includes('claims')) {
      uploadPath = path.join(uploadDir, 'documents');
    }

    cb(null, uploadPath);
  },
  filename: (req: Request, file: Express.Multer.File, cb) => {
    // Generate unique filename with timestamp and random string
    const timestamp = Date.now();
    const randomString = Math.random().toString(36).substring(2, 15);
    const extension = path.extname(file.originalname);
    const baseName = path.basename(file.originalname, extension).replace(/[^a-zA-Z0-9]/g, '-');

    const filename = `${baseName}-${timestamp}-${randomString}${extension}`;
    cb(null, filename);
  }
});

// File filter to validate file types
const fileFilter = (req: Request, file: Express.Multer.File, cb: multer.FileFilterCallback) => {
  // Allowed file types for gold consignment documentation
  const allowedMimeTypes = [
    'image/jpeg',
    'image/jpg', 
    'image/png',
    'image/webp',
    'application/pdf',
    'application/msword',
    'application/vnd.openxmlformats-officedocument.wordprocessingml.document'
  ];

  const allowedExtensions = ['.jpg', '.jpeg', '.png', '.webp', '.pdf', '.doc', '.docx'];

  const fileExtension = path.extname(file.originalname).toLowerCase();

  if (allowedMimeTypes.includes(file.mimetype) && allowedExtensions.includes(fileExtension)) {
    cb(null, true);
  } else {
    cb(new Error(`Invalid file type. Allowed types: ${allowedExtensions.join(', ')}`));
  }
};

// Configure multer with size limits and validation
export const uploadMiddleware = multer({
  storage: storage,
  fileFilter: fileFilter,
  limits: {
    fileSize: 10 * 1024 * 1024, // 10MB per file
    files: 10, // Maximum 10 files per upload
  },
});

// File validation service
class FileUploadService {

  /**
   * Validate uploaded file
   */
  validateFile(file: Express.Multer.File): boolean {
    if (!file) return false;

    // Check file size (10MB limit)
    if (file.size > 10 * 1024 * 1024) {
      throw new Error('File size too large. Maximum size is 10MB.');
    }

    // Check file type
    const allowedTypes = ['image/jpeg', 'image/png', 'image/webp', 'application/pdf'];
    if (!allowedTypes.includes(file.mimetype)) {
      throw new Error('Invalid file type. Only JPEG, PNG, WebP, and PDF files are allowed.');
    }

    return true;
  }

  /**
   * Get secure file URL
   */
  getFileUrl(filename: string, category: string = 'documents'): string {
    return `/api/files/${category}/${filename}`;
  }

  /**
   * Delete file from storage
   */
  async deleteFile(filepath: string): Promise<void> {
    try {
      const fullPath = path.join(process.cwd(), filepath);
      if (fs.existsSync(fullPath)) {
        fs.unlinkSync(fullPath);
      }
    } catch (error) {
      console.error('Failed to delete file:', error);
      throw new Error('File deletion failed');
    }
  }

  /**
   * Move file to different category
   */
  async moveFile(currentPath: string, newCategory: string, filename: string): Promise<string> {
    try {
      const oldPath = path.join(process.cwd(), currentPath);
      const newPath = path.join(process.cwd(), 'uploads', newCategory, filename);

      if (fs.existsSync(oldPath)) {
        fs.renameSync(oldPath, newPath);
        return `/uploads/${newCategory}/${filename}`;
      }

      throw new Error('Source file not found');
    } catch (error) {
      console.error('Failed to move file:', error);
      throw new Error('File move operation failed');
    }
  }

  /**
   * Scan file for viruses (placeholder for future implementation)
   */
  async scanFile(filepath: string): Promise<boolean> {
    // In production, this would integrate with antivirus scanning
    // For now, return true (file is clean)
    // Could integrate with services like ClamAV, VirusTotal API, etc.

    try {
      // Basic file validation
      const stats = fs.statSync(filepath);

      // Check if file is not empty
      if (stats.size === 0) {
        throw new Error('Empty file detected');
      }

      // Additional security checks could be added here
      // - File signature validation
      // - Malware scanning
      // - Content analysis

      return true;
    } catch (error) {
      console.error('File scan failed:', error);
      return false;
    }
  }

  /**
   * Get file metadata
   */
  getFileMetadata(file: Express.Multer.File) {
    return {
      originalName: file.originalname,
      filename: file.filename,
      mimetype: file.mimetype,
      size: file.size,
      uploadDate: new Date(),
      path: file.path,
    };
  }

  /**
   * Clean up temporary files
   */
  async cleanupTempFiles(maxAge: number = 24 * 60 * 60 * 1000): Promise<void> {
    try {
      const tempDir = path.join(process.cwd(), 'uploads', 'temp');

      if (!fs.existsSync(tempDir)) return;

      const files = fs.readdirSync(tempDir);
      const now = Date.now();

      for (const file of files) {
        const filePath = path.join(tempDir, file);
        const stats = fs.statSync(filePath);

        if (now - stats.mtime.getTime() > maxAge) {
          fs.unlinkSync(filePath);
        }
      }
    } catch (error) {
      console.error('Cleanup failed:', error);
    }
  }
}

export const fileUploadService = new FileUploadService();

// Error handling middleware for multer
export const handleUploadError = (error: any, req: any, res: any, next: any) => {
  if (error instanceof multer.MulterError) {
    if (error.code === 'LIMIT_FILE_SIZE') {
      return res.status(400).json({
        message: 'File too large. Maximum size is 10MB per file.',
      });
    }

    if (error.code === 'LIMIT_FILE_COUNT') {
      return res.status(400).json({
        message: 'Too many files. Maximum 10 files per upload.',
      });
    }

    if (error.code === 'LIMIT_UNEXPECTED_FILE') {
      return res.status(400).json({
        message: 'Unexpected file field.',
      });
    }
  }

  if (error.message.includes('Invalid file type')) {
    return res.status(400).json({
      message: error.message,
    });
  }

  return res.status(500).json({
    message: 'File upload failed. Please try again.',
  });
};

// Utility function to create safe filenames
export function createSafeFilename(originalName: string): string {
  const extension = path.extname(originalName);
  const baseName = path.basename(originalName, extension);

  // Remove special characters and replace with hyphens
  const safeName = baseName
    .replace(/[^a-zA-Z0-9]/g, '-')
    .replace(/-+/g, '-')
    .replace(/^-|-$/g, '');

  return `${safeName}${extension}`;
}

// Export configured multer instance
export default uploadMiddleware;

server/services/goldPrice.ts.content:
interface GoldPrices {
  usd: number;
  gbp: number;
  eur: number;
  change24h: {
    usd: number;
    gbp: number;
    eur: number;
  };
  lastUpdated: Date;
}

class GoldPriceService {
  private cachedPrices: GoldPrices | null = null;
  private lastFetch: Date | null = null;
  private readonly CACHE_DURATION = 5 * 60 * 1000; // 5 minutes

  async getCurrentPrices(): Promise<GoldPrices> {
    // Check if we have cached data that's still fresh
    if (this.cachedPrices && this.lastFetch && 
        Date.now() - this.lastFetch.getTime() < this.CACHE_DURATION) {
      return this.cachedPrices;
    }

    try {
      // In a real implementation, you would call actual gold price APIs
      // For example: MetalsAPI, LBMA, or similar services
      const prices = await this.fetchFromAPI();

      this.cachedPrices = prices;
      this.lastFetch = new Date();

      return prices;
    } catch (error) {
      console.error('Failed to fetch gold prices:', error);

      // Return cached data if available, otherwise return default prices
      if (this.cachedPrices) {
        return this.cachedPrices;
      }

      // Fallback to default prices
      return this.getDefaultPrices();
    }
  }

  private async fetchFromAPI(): Promise<GoldPrices> {
    // This would typically call a real gold price API
    // Example APIs: MetalsAPI, CurrencyAPI, LBMA, etc.

    const API_KEY = process.env.METALS_API_KEY || process.env.GOLD_PRICE_API_KEY;

    if (API_KEY) {
      try {
        // Example implementation for MetalsAPI
        const response = await fetch(`https://api.metals.live/v1/spot/gold`, {
          headers: {
            'Authorization': `Bearer ${API_KEY}`,
          },
        });

        if (response.ok) {
          const data = await response.json();
          return this.transformAPIResponse(data);
        }
      } catch (error) {
        console.error('API request failed:', error);
      }
    }

    // Simulate realistic price movements around base prices
    const baseUSD = 2034.50;
    const variation = (Math.random() - 0.5) * 40; // Â±$20 variation
    const usdPrice = baseUSD + variation;

    // Calculate other currencies with realistic exchange rates
    const gbpPrice = usdPrice * 0.8; // Approximate GBP rate
    const eurPrice = usdPrice * 0.92; // Approximate EUR rate

    // Simulate 24h changes
    const usdChange = (Math.random() - 0.5) * 4; // Â±2% change
    const gbpChange = usdChange + (Math.random() - 0.5) * 0.5; // Slight variation
    const eurChange = usdChange + (Math.random() - 0.5) * 0.5; // Slight variation

    return {
      usd: parseFloat(usdPrice.toFixed(2)),
      gbp: parseFloat(gbpPrice.toFixed(2)),
      eur: parseFloat(eurPrice.toFixed(2)),
      change24h: {
        usd: parseFloat(usdChange.toFixed(2)),
        gbp: parseFloat(gbpChange.toFixed(2)),
        eur: parseFloat(eurChange.toFixed(2)),
      },
      lastUpdated: new Date(),
    };
  }

  private transformAPIResponse(data: any): GoldPrices {
    // Transform the API response to our standard format
    // This would depend on the specific API being used
    return {
      usd: data.price_usd || 2034.50,
      gbp: data.price_gbp || 1627.80,
      eur: data.price_eur || 1885.40,
      change24h: {
        usd: data.change_24h_usd || 1.2,
        gbp: data.change_24h_gbp || 0.8,
        eur: data.change_24h_eur || -0.3,
      },
      lastUpdated: new Date(),
    };
  }

  private getDefaultPrices(): GoldPrices {
    return {
      usd: 2034.50,
      gbp: 1627.80,
      eur: 1885.40,
      change24h: {
        usd: 1.2,
        gbp: 0.8,
        eur: -0.3,
      },
      lastUpdated: new Date(),
    };
  }

  // Method to get historical prices (for future implementation)
  async getHistoricalPrices(days: number): Promise<any[]> {
    // This would implement historical price fetching
    // For now, return empty array
    return [];
  }
}

export const goldPriceService = new GoldPriceService();

